mapper.select:
select userid , userpwd , first_name , last_name
from users
where userid=#{userid}

mapper.update:
update users
	set userpwd=#{userpwd},
	first_name=#{first_name},
	last_name=#{last_name}
where userid=#{userid}

mapper.select:
select st.storeid, st.name as storename, st.phone, st.address, rs.name as username, rs.cnt, rs.price, rs.sdate, rs.edate
from store st inner join reservation rs on st.storeid = rs.storeid inner join users us on us.userid = rs.userid
where us.userid = #{userid}
