mapper.select:
select userid , userpwd , first_name , last_name
from users
where userid=#{userid}

mapper.update:
update users
	set userpwd=#{userpwd},
	first_name=#{first_name},
	last_name=#{last_name}
where userid=#{userid}

mapper.select:
select concat(concat(last_name, ' '), first_name) as fullname 
from users
where userid=#{userid}
